import React from 'react';

const style = {
    textAlign: 'center', height: '50vh', display: 'flex', alignItems: 'center', justifyContent: 'center'
}
const About = () => {
    return (
        <div style={style}>
            <div>
                <h2>I'm Burhan Uddin</h2>
                <p>I'm a student. I passed Higher Secondary level in 2021. <br />
                    I'm preparing myself for admission. <br /> Besides I'm learning web development from Jhankar Mahbub sir. <br /> My goal is to become CEO of Google or Microsoft. -_-
                </p>

            </div>
        </div>
    );
};

export default About;
